package t1071

import "testing"

func TestGcdOfStrings(t *testing.T) {
	data := []struct {
		name     string
		str1     string
		str2     string
		expected string
	}{
		{"1", "ABCABC", "ABC", "ABC"},
		{"2", "ABABAB", "ABAB", "AB"},
		{"3", "LEET", "CODE", ""},
		{"4", "ABABABAB", "ABAB", "ABAB"},
		{
			"5",
			"TAUXXTAUXXTAUXXTAUXXTAUXX",
			"TAUXXTAUXXTAUXXTAUXXTAUXXTAUXXTAUXXTAUXXTAUXX",
			"TAUXX",
		},
		{
			"5",
			"NLZGMNLZGMNLZGMNLZGMNLZGMNLZGMNLZGMNLZGM",
			"NLZGMNLZGMNLZGMNLZGMNLZGMNLZGMNLZGMNLZGMNLZGM",
			"NLZGM",
		},
		{
			"6",
			"",
			"",
			"",
		},
		{
			"7",
			"A",
			"AA",
			"A",
		},
	}
	for _, d := range data {
		t.Run(d.name, func(t *testing.T) {
			resp := gcdOfStrings(d.str1, d.str2)
			if resp != d.expected {
				t.Errorf("⚠️ Test `%s` failed! Expeceted %s, but got %s!", d.name, d.expected, resp)
			}
		})
	}
}
